import { Button, Input, InputAdornment, InputBase, InputLabel } from '@mui/material';
import React, { useEffect, useMemo, useRef, useState } from 'react'
import PostService from './api/PostService';
import FilmList from './components/FilmsList';
import Header from './components/Header';
import Search from './components/Search';
import { useFetching } from './hooks/useFetching';
import {useObserver} from './hooks/useObserver'
import './style/App.css'

function App() {

  const [films, setFilms] = useState([])
  const [page, setPage] = useState(1)
  const [totalPages, setTotalPages] = useState(3)
  const [search, setSearch] = useState()
  const [filter,setFilter] = useState('')


  const lastElement = useRef()




  const [fetchPosts, isPostsLoading, postError] = useFetching(async () => {

    const posts = await PostService.getAll(page);
      setFilms([...films, ...posts])

  })



  useEffect(() => {
    fetchPosts();
    }, [page])



    // const searchFilm = function() {

      // setFilms(films.forEach(film => {
      //     if (film.nameRu) {
      //     film.nameRu.toLowerCase().includes(filter)
      //   }
      // }))


    //   let test = [...films].filter(film => {

    //       if (film.nameRu) {
    //         film.nameRu.toLowerCase().includes(filter)
    //       }

    //     })
    //     setFilms(test)

    // }

    const searchedFilms = useMemo(() => {

      return [...films].filter(film => {
        if(film.nameRu) {
          return film.nameRu.toLowerCase().includes(filter)
        }
      })

    }, [filter, films])



    useObserver(lastElement, page < totalPages, isPostsLoading, () => {
      if(films.length) {setPage(page+1)};
    })

  return (
    <div className="App">
      <Header></Header>
      <Search
      filter={filter}
      setFilter={setFilter}
      />
      <div className='films__container'>
        { searchedFilms &&
        searchedFilms.map((film) => 
          <FilmList 
            key={film.kinopoiskId+Date.now()}
            image={film.posterUrl}
            name={film.nameRu}
            countries={film['countries'][0]}
            year={film.year}
            >
          </FilmList>
          )}

      </div>
      <div 
      ref={lastElement} 
      style={{height:'20px', background: 'teal'}}>
      </div>
    </div>
  );
}

export default App;
