import React, { useState } from "react";
import { useParams } from "react-router-dom";
import PostService from "../api/PostService";
import { useFetching } from "../hooks/useFetching";

const FilmIdPage = function() {

    const params = useParams()


    // Создаем состояние, в которое поместим то, что вернет сервер по id
    const [post, setPost] = useState({});
    // Данный callback возвращает нам параметры его содержащие в первом элементе массива
    const [fetchPostById, isLoading, error] = useFetching(async(id) => { 
        const response = await PostService.getById(params.id);
        setPost(response)
        console.log(response.data)
    })
    // Вызываем функцию, что вернул callback
    useEffect(() => {
        fetchPostById(params.id) 
    }, [])

    return(
        <div> Страница фильма с ID = {params.id} </div>
    )
}

export default FilmIdPage;
