import React, { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import PostService from "../api/PostService";
import { ArrContext } from "../context";
import { useFetching } from "../hooks/useFetching";

const FilmIdPage = function() {

    const params = useParams()
    const [film, setFilm] = useState({})
    const {films, setFilms} = useContext(ArrContext)
    
    let copy = films
    useEffect(() => {
        setFilm( copy.filter(el => {
            if(el.kinopoiskId==params.id) {
                return el
            }
        })[0])
    }, [])

    console.log(film)
    
    return(
        <div className="filmId__wrapper"> 
            <div className="filmId__block">
                <h1>
                    {film.nameRu}
                </h1>
    
                 <img className="filmId__img" src={film.posterUrl}/>
                   
            <div>Жанр: {film.genres[0].genre}</div> 
            <div>Жанр: {film.genres[0].genre}</div>    
                
                
            </div>
        </div>
    )
}

export default FilmIdPage;
